const data = $input.all().map(item => item.json.data)

const activity = data.map(item => {return {date:item.date,recentActivity:item.recentActivity}})

return activity;

const last_post = $input.all().flatMap(item => item.json.data)

const filter_last_post = last_post.map(item => {return {text:item.text,postUrl:item.postUrl,postedAt:item.postedAt,postedDate:item.postedDate}})

return [{dernier_post:filter_last_post[0]}];

// Récupère tous les éléments d'entrée
const tousLesElements = $input.all();

// Filtre les éléments pour ne garder que ceux dont 'data' contient 'geo'
// Puis, restructure chaque objet pour inclure uniquement 'connection', 'follower' et 'data.geo'
const resultat = tousLesElements
  .filter(item => item.json.data && item.json.data.geo)
  .map(item => ({
    connection: item.json.connection,
    follower: item.json.follower,
    position: {
      geo: item.json.data.geo
    }
  }));

// Retourne le tableau restructuré
return resultat;

